int CheckWin(char board[3][3])
{
    for (int i = 0; i < 3; i++) {
        if (board[i][0] != ' ' && board[i][0] == board[i][1] && board[i][1] == board[i][2])
            return board[i][0];
        if (board[0][i] != ' ' && board[0][i] == board[1][i] && board[1][i] == board[2][i])
            return board[0][i];
    }
    if (board[0][0] != ' ' && board[0][0] == board[1][1] && board[1][1] == board[2][2])
        return board[0][0];
    if (board[0][2] != ' ' && board[0][2] == board[1][1] && board[1][1] == board[2][0])
        return board[0][2];
    return 0;
}
int IsDraw(char board[3][3])
{
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            if (board[i][j] == ' ')
                return 0;
        }
    }
    return 1;
}
void AIMove(char board[3][3], int* r, int* c) {
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            if (board[i][j] == ' ') {
                board[i][j] = 'O';
                if (CheckWin(board) == 'O') {
                    *r = i;
                    *c = j;
                    return;
                }
                board[i][j] = ' ';
            }
        }
    }

    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            if (board[i][j] == ' ') {
                board[i][j] = 'X';
                if (CheckWin(board) == 'X') {
                    board[i][j] = 'O';
                    *r = i;
                    *c = j;
                    return;
                }
                board[i][j] = ' ';
            }
        }
    }

    if (board[1][1] == ' ') {
        *r = 1;
        *c = 1;
        return;
    }

    int corners[4][2] = { {0,0}, {0,2}, {2,0}, {2,2} };
    for (int i = 0; i < 4; i++) {
        int ri = corners[i][0];
        int ci = corners[i][1];
        if (board[ri][ci] == ' ') {
            *r = ri;
            *c = ci;
            return;
        }
    }

    int sides[4][2] = { {0,1}, {1,0}, {1,2}, {2,1} };
    for (int i = 0; i < 4; i++) {
        int ri = sides[i][0];
        int ci = sides[i][1];
        if (board[ri][ci] == ' ') {
            *r = ri;
            *c = ci;
            return;
        }
    }
}
void DrawMarks(char screenArray[1000][1000], char board[3][3], int startRow, int startCol) {
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            int r = startRow + i * 6;
            int c = startCol + j * 13 + 4;
            if (board[i][j] == 'X') {
                SpriteHead(screenArray, r + 2, c);
            }
            else if (board[i][j] == 'O') {
                RobotHead(screenArray, r, c - 2);
            }
        }
    }
}
